mapper.insert:
insert into tj_hchbdb_warnconditionvoc (
	status
	,id
	 , equipmentid
	, hydrocarbon
	, rate
	, temperature
	, pressure
	, humidity
	, wastegas
)values(
	1
	, #{id}
	 , #{equipmentid}
	, #{hydrocarbon}
	,#{rate}
	, #{temperature}
	, #{pressure}
	, #{humidity}
	, #{wastegas}
)

mapper.update:
update tj_hchbdb_warnconditionvoc
equipmentid=#{equipmentid}, hydrocarbon=#{hydrocarbon}, rate=#{rate}, temperature=#{temperature}, pressure=#{pressure}, humidity=#{humidity}, wastegas=#{wastegas}, status=#{status}
where id=#{id}

mapper.select:
select w.*, c.id as companyid, c.name as companyname
from tj_hchbdb_warnconditionvoc w
left join tj_hchbdb_equipment e
	on e.eid = w.equipmentid
left join tj_hchbdb_companyInfo_equipment aue 
		on e.id = aue.equipmentid 
left join tj_hchbdb_companyInfo c 
	on aue.companyid = c.id

	1=1
	 and w.id=#{id}
	 and w.equipmentid=#{equipmentid}
	 and w.hydrocarbon=#{hydrocarbon}
	 and w.rate = ${rate} 
	 and w.temperature=#{temperature}
	 and w.pressure=#{pressure}
	 and w.humidity=#{humidity}
	 and w.wastegas=#{wastegas}
	 and w.status=#{status}

select w.*, c.id as companyid, c.name as companyname
from tj_hchbdb_warnconditionvoc w
left join tj_hchbdb_equipment e
	on e.eid = w.equipmentid
left join tj_hchbdb_companyInfo_equipment aue 
		on e.id = aue.equipmentid 
left join tj_hchbdb_companyInfo c 
	on aue.companyid = c.id

	1=1
	 c.name like '%${companyname}%'
	 and w.id=#{id}
	 and w.equipmentid=#{equipmentid}
	 and w.hydrocarbon=#{hydrocarbon}
	 and w.rate = ${rate} 
	 and w.temperature=#{temperature}
	 and w.pressure=#{pressure}
	 and w.humidity=#{humidity}
	 and w.wastegas=#{wastegas}
	 and w.status=#{status}

select w2.n, w1.* 
from (
	select w.*, c.id as companyid, c.name as companyname
	from tj_hchbdb_warnconditionvoc w
	left join tj_hchbdb_equipment e
		on e.eid = w.equipmentid
	left join tj_hchbdb_companyInfo_equipment aue 
			on e.id = aue.equipmentid 
	left join tj_hchbdb_companyInfo c 
		on aue.companyid = c.id

	where c.name like '%${w.companyname}%'

) w1, (
	select top ${endrow} row_number() over (order by m.sort desc) n, m.id	
	from ( 
		select *
		from tj_hchbdb_warnconditionvoc 
		where
			1=1
			 and id=#{w.id}
			 and equipmentid=#{w.equipmentid}
			 and hydrocarbon=#{w.hydrocarbon}
			 and rate = ${w.rate} 
			 and temperature=#{w.temperature}
			 and pressure=#{w.pressure}
			 and humidity=#{w.humidity}
			 and wastegas=#{w.wastegas}
			 and status=#{w.status}
		)m 
	) w2 
where w1.id = w2.id and w2.n > ${startrow} order by w2.n asc
